mapper.select:
select a.store_code storeId, a.store_name storeName, b.phone phone, a.store_address address, b.name managerName, a.invitation_code invitationCode, b.user_name storeAccount, a.version version from t_store_info a left join t_sys_user b on a.user_code = b.user_code where a.is_delete = 0
and a.user_code = #{userId}
and a.store_code like concat('%', #{storeId}, '%')
and a.store_name like concat('%', #{storeName}, '%')
and b.name like concat('%', #{managerName}, '%')
and a.province_id = #{provinceId}
and a.city_id = #{cityId}
and a.area_id = #{areaId}
order by a.create_time desc

select count(store_code)
from t_store_info
where is_delete = 0
and (invitation_code = #{invitationCode}
  or license_code = #{licenseCode}
  or user_code = #{userId})

    and store_code != #{storeId}
